	(184, 'フュメド・ポワソン', '', 'https://hocbnifuactbvmyjraxy.supabase.co/storage/v1/object/public/recipe-images/sauce_default_1768373708329.jpg', '', '', '', '{owner:admin,ソース}', '[{"type": "normal", "_meta": true, "groups": [{"id": "default", "name": "材料"}], "stepGroups": [{"id": "default", "name": "作り方", "count": 14}]}, {"cost": "", "name": "魚のフュメ(スユエあり)", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "白身魚のアラ", "unit": "kg", "groupId": "default", "quantity": 5, "purchaseCost": ""}, {"cost": "", "name": "タマネギ", "unit": "g", "groupId": "default", "quantity": 200, "purchaseCost": ""}, {"cost": "", "name": "ニンジン", "unit": "g", "groupId": "default", "quantity": "50", "purchaseCost": ""}, {"cost": "", "name": "セロリ", "unit": "g", "groupId": "default", "quantity": 100, "purchaseCost": 850}, {"cost": "", "name": "ポワロー", "unit": "g", "groupId": "default", "quantity": 200, "purchaseCost": 1800}, {"cost": "", "name": "エシャロット", "unit": "g", "groupId": "default", "quantity": 100, "purchaseCost": ""}, {"cost": "", "name": "シャンピニョン・ド・パリ", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "ブーケガルニ 少量", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "粗塩 適量", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "オリーブ油 適量", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "バター 少量", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "白ワイン", "unit": "ml", "groupId": "default", "quantity": 1500, "purchaseCost": ""}, {"cost": "", "name": "水", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}]', '{鯛、舌ビラメ、スズキなど白身魚のアラを用意する。最低30分間は水にさらし、血合いや骨の間の汚れを取り除く。よく水気をきっておく。,タマネギ、ニンジン、セロリ、ポワロー、シャンピニョン・ド・パリ、エシャロットはすべて厚さ2~3mmのエマンセにする。,鍋を中火にかけ、オリーブ油を引き、風味づけに少量のバターを入れる。野菜を一度に加える。,野菜全体に油が行き渡ったら、香りを引き出すようにスュエする。,野菜がしんなりし、タマネギが透き通ってきたら白身魚のアラを加える。