	(68, 'ブラバスソース(bravas)', 'Tapas', 'https://hocbnifuactbvmyjraxy.supabase.co/storage/v1/object/public/recipe-images/b1637853-5012-4590-86c1-af76cef7b61f_0.jpg', '', '', '', '{owner:admin,ソース}', '[{"type": "normal", "_meta": true, "groups": [{"id": "default", "name": "ブラバスソース"}], "stepGroups": [{"id": "default", "name": "作り方", "count": 3}]}, {"cost": "", "name": "トマト", "unit": "g", "groupId": "default", "quantity": "300", "vendorRef": null, "purchaseCost": "", "purchaseCostRef": null}, {"cost": "", "name": "にんにく (皮付き)", "unit": "かけ", "groupId": "default", "quantity": 2, "purchaseCost": ""}, {"cost": "", "name": "赤パプリカ ", "unit": "g", "groupId": "default", "quantity": "100", "vendorRef": null, "purchaseCost": 235, "purchaseCostRef": null}, {"cost": "", "name": "カイエンペッパー ", "unit": "g", "groupId": "default", "quantity": "1", "vendorRef": null, "purchaseCost": "", "purchaseCostRef": null}, {"cost": "", "name": "オリーブオイル", "unit": "g", "groupId": "default", "quantity": 10, "purchaseCost": ""}, {"cost": "", "name": "塩", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "白こしょう", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}]', '{"じゃがいもは皮ごと、竹串が通る位までゆでる。スチームコンベクションを使ってもよい。じゃがいもが冷めてから2.5cm幅に切り、それを直径3cmのセルクル (円形の抜き型) で抜く。さらに上面直径1.5cm位にくり抜いて中央にくぼみをつくり、170度℃の油で揚げて塩をふっておく。ブラバソースをつくる。","トマトは半分に切り、オリーブオイルを塗ったパプリカ、にんにくと共に、200℃のオーブンで約20分焼く。焼き上がったら、皮をむいたにんにくと種を取り除いたパプリカ、トマトをオリーブオイルで軽く炒める。さらにカイエンペッパー、塩、こしょうを入れ少し煮詰めていく。煮詰まったらミキサーにかけ、シノワ (円錐の形をしたこし器)でこす。少し固めのソースになる。",アイオリソースをつくる。アイオリソースのすべての材料を混ぜ合わせる。じゃがいもの中央のくぼみにブラバソースをたっぷり入れ、その上にアイオリソースを山のようにのせて、あたたかいうちにいただく。}', '2026-01-03 18:51:22.465911+00', '', 'ソース', 'BISTRO CAVACAVA', 183),
	(129, 'カスタードクリーム(真空調理)', '真空調理で作る', 'https://hocbnifuactbvmyjraxy.supabase.co/storage/v1/object/public/recipe-images/a1ebf92a-39d5-43de-a6ae-1d57019d4e96_0.jpg', '', '', '', '{owner:admin,"デザート,お菓子,ソース"}', '[{"type": "normal", "_meta": true, "groups": [{"id": "default", "name": "材料"}], "stepGroups": [{"id": "default", "name": "作り方", "count": 1}]}, {"cost": "", "name": "卵黄", "unit": "個", "groupId": "default", "quantity": "1", "vendorRef": null, "purchaseCost": "", "purchaseCostRef": null}, {"cost": 9, "name": "グラニュー糖", "unit": "g", "groupId": "default", "quantity": "30", "vendorRef": "有限会社ミナト商会", "purchaseCost": 290, "purchaseCostRef": 290}, {"cost": 2, "name": "薄力粉", "unit": "g", "groupId": "default", "quantity": "8", "vendorRef": "有限会社ミナト商会", "purchaseCost": 280, "purchaseCostRef": 280}, {"cost": 25, "name": "牛乳", "unit": "g", "groupId": "default", "quantity": "100", "vendorRef": "株式会社東京めいらく", "purchaseCost": 245, "purchaseCostRef": 245}]', '{85℃で20分}', '2026-01-03 18:51:48.244736+00', '', 'デザート,お菓子,ソース', 'BISTRO CAVACAVA', 150),
	(117, 'アングレーズ', '', 'https://hocbnifuactbvmyjraxy.supabase.co/storage/v1/object/public/recipe-images/8da5fafb-6e75-41de-aeb4-accc83e37b2e_0.jpg', '', '', '', '{owner:admin,"ソース,デザート",デザートソース}', '[{"type": "normal", "_meta": true, "groups": [{"id": "default", "name": "材料"}], "stepGroups": [{"id": "default", "name": "作り方", "count": 7}]}, {"cost": "", "name": "牛乳 .", "unit": "ml", "groupId": "default", "quantity": 200, "purchaseCost": ""}, {"cost": "", "name": "バニラのさや.", "unit": "本", "groupId": "default", "quantity": 1, "purchaseCost": ""}, {"cost": "", "name": "卵黄.", "unit": "個分", "groupId": "default", "quantity": 3, "purchaseCost": ""}, {"cost": "", "name": "グラニュー糖.", "unit": "g", "groupId": "default", "quantity": 45, "purchaseCost": ""}]', '{牛乳と縦に裂いたバニラのさやを鍋に入れて火にかけ、沸騰する直前まで温めます。バニラのさやではなくバニラエッセンスを使う場合は、熱で香りが飛んでしまうため、後で加えます。,ボウルに卵黄とグラニュー糖を入れて、ホイッパー（泡立て器）で白っぽくなるまですり混ぜます。,1を火から下ろしたら、ラップや蓋をして十分にバニラの香りを抽出します。1と2を合わせて、卵黄が固まらないように少しずつ混ぜます。,3を弱火で熱しながら、ダマにならないようにゆっくりと8の字を描くようにかき混ぜます。沸騰させないように注意して、常にかき混ぜながら温めるのがコツです。,温度が83℃に達すると卵に火が入り凝固し始めるので、少しとろみが出てきたら火から下ろします。83℃に到達後、かき混ぜながら余熱でその温度を約3分保つことで、高い殺菌効果が期待できます。,ザルでこしながら他のボウルに移し、氷水で冷やせば完成です。バニラエッセンスを使う場合は、仕上げとしてここで2、3滴ほど加えます。,ちなみに、アングレーズソースが温かいうちに、あらかじめ溶かしておいた適量のチョコレートを4回程度に分けてゆっくりと注ぎ、艶が出るまできちんと混ぜ合わせれば、美しい艶のチョコレートソースが出来上がります。こちらもぜひ試してみてください。}', '2026-01-03 18:51:41.989459+00', 'デザートソース', 'ソース,デザート', 'BISTRO CAVACAVA', 161),
	(477, '焦がしバターのパルフェ', '仕上がり:約500ml(パウンド型1本分または個別カップ6~8個)', '', NULL, NULL, '', '{デザート,owner:d03ce203-5e8c-4123-8f90-b8e56b38ae69}', '[{"type": "normal", "_meta": true, "groups": [{"id": "f8e7ff6b-8f43-4648-815b-90ed98b2d49a", "name": "焦がしバター"}, {"id": "5be57e14-cc19-4a5d-a0a7-85a2f6ce4ee5", "name": "パータボンブ"}, {"id": "f3b2042b-dfd3-48a9-9671-3f45aa238936", "name": "クリーム"}, {"id": "d0c1231b-f70d-477d-aa51-aff252aea092", "name": "仕上げ(任意)"}], "stepGroups": [{"id": "a41efcd0-450b-45d1-b6a9-1206d0d1e168", "name": "作り方", "count": 1}]}, {"cost": "", "name": "無塩バター", "unit": "g", "group": "焦がしバター", "groupId": "f8e7ff6b-8f43-4648-815b-90ed98b2d49a", "quantity": "80", "purchaseCost": ""}, {"cost": "", "name": "卵黄", "unit": "個", "group": "パータボンブ", "groupId": "5be57e14-cc19-4a5d-a0a7-85a2f6ce4ee5", "quantity": "3", "purchaseCost": ""}, {"cost": 17.4, "name": "グラニュー糖", "unit": "g", "group": "パータボンブ", "groupId": "5be57e14-cc19-4a5d-a0a7-85a2f6ce4ee5", "quantity": "60", "isAlcohol": false, "itemCategory": "food", "purchaseCost": 290}, {"cost": "", "name": "水", "unit": "ml", "group": "パータボンブ", "groupId": "5be57e14-cc19-4a5d-a0a7-85a2f6ce4ee5", "quantity": "20", "isAlcohol": false, "itemCategory": "food", "purchaseCost": ""}, {"cost": "", "name": "生クリーム(乳脂肪35%以上)", "unit": "ml", "group": "クリーム", "groupId": "f3b2042b-dfd3-48a9-9671-3f45aa238936", "quantity": "200", "purchaseCost": ""}, {"cost": "", "name": "塩", "unit": "ひとつまみ", "group": "クリーム", "groupId": "f3b2042b-dfd3-48a9-9671-3f45aa238936", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "フルール・ド・セル、キャラメルソース、ローストナッツなど", "unit": "", "group": "仕上げ(任意)", "groupId": "d0c1231b-f70d-477d-aa51-aff252aea092", "quantity": null, "purchaseCost": ""}]', '{""}', '2026-02-07 01:03:31.788541+00', 'デザート', 'デザート', 'BISTRO CAVACAVA', NULL),
	(275, 'チョコレートと抹茶のテリーヌ', '', 'https://www.chilltea-tokyo.com/wp-content/uploads/2021/04/20210227_chilltea5190_re3-scaled.jpg', NULL, NULL, '', '{owner:admin,デザート}', '[{"type": "normal", "_meta": true, "groups": [{"id": "db5a7ea6-0955-498a-ad3e-2b0394fd66be", "name": "材料"}], "stepGroups": [{"id": "default", "name": "作り方", "count": 10}]}, {"cost": "", "name": "ホワイトチョコレート", "unit": "g", "groupId": "db5a7ea6-0955-498a-ad3e-2b0394fd66be", "quantity": "160", "purchaseCost": ""}, {"cost": "", "name": "無塩バター", "unit": "g", "groupId": "db5a7ea6-0955-498a-ad3e-2b0394fd66be", "quantity": "80", "purchaseCost": ""}, {"cost": "", "name": "抹茶", "unit": "g", "groupId": "db5a7ea6-0955-498a-ad3e-2b0394fd66be", "quantity": "10", "purchaseCost": 1820}, {"cost": "", "name": "卵", "unit": "個", "groupId": "db5a7ea6-0955-498a-ad3e-2b0394fd66be", "quantity": "3", "purchaseCost": ""}, {"cost": "", "name": "砂糖", "unit": "g", "groupId": "db5a7ea6-0955-498a-ad3e-2b0394fd66be", "quantity": "50", "purchaseCost": ""}, {"cost": "", "name": "小麦粉", "unit": "g", "groupId": "db5a7ea6-0955-498a-ad3e-2b0394fd66be", "quantity": "5", "purchaseCost": ""}, {"cost": "", "name": "クリーム", "unit": "ml", "groupId": "db5a7ea6-0955-498a-ad3e-2b0394fd66be", "quantity": "100", "purchaseCost": ""}]', '{クリームを鍋に入れて弱火で温め、沸騰し始めたら火を止めるように注意してください。,クリーム、ホワイトチョコレート、溶かしバターをボウルに入れます。ゴムベラでゆっくり混ぜ、ホワイトチョコレートが完全に溶けるまで混ぜる。（*完全に溶けにくい場合は、湯煎にかけて混ぜる）,湯煎にかけたボウルで、卵と砂糖を混ぜ合わせます。準備が温かくなるまで、力強く泡立て器で混ぜる。,卵の混合物をチョコレート混合物に2、3回に分けて加え、滑らかでつやのある生地になるまで優しく混ぜ合わせる。,ふるった小麦粉と抹茶パウダーを加えます。粉っぽさがなくなるまで混ぜ合わせる。,準備した生地をパウンドケーキ型に流し込む。,型を縁の高い天板の中央に置きます。型の表面をアルミホイルで覆い、天板に約1cmの高さまで熱湯を注ぐ。,150°Cで60分間焼く。,冷ましてからアルミホイルを取り除き、ラップで覆い、冷蔵庫で一晩休ませまる。,型から外し、湯で温めたナイフで切る。}', '2026-01-18 06:14:30.199398+00', 'デザート', 'デザート', 'BISTRO CAVACAVA', 71),
	(27, 'ガトーバスク(Pastel vasco)', '厚めのクッキー生地にカスタードクリームやさくらんぼのジャムをサンドして焼き上げた素朴で贅沢な味わいが特徴です。表面に格子模様や、バスク十字「ローブリュー(Lauburu)」が描かれることが多く、サクッとしっとりとした食感と香ばしい風味が魅力で、アーモンドパウダー入りの生地とクリームの一体感が楽しめます。 ', 'https://hocbnifuactbvmyjraxy.supabase.co/storage/v1/object/public/recipe-images/1768710264330-sqxxcv.jpg', '', '', '', '{owner:admin,"URL取り込み,お菓子,デザート",プティフール}', '[{"type": "normal", "_meta": true, "groups": [{"id": "default", "name": "材料"}], "stepGroups": [{"id": "default", "name": "作り方", "count": 13}]}, {"cost": "", "name": "タルト生地", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "バター", "unit": "g", "groupId": "default", "quantity": 165, "purchaseCost": ""}, {"cost": "", "name": "塩", "unit": "g", "groupId": "default", "quantity": 1.2, "purchaseCost": ""}, {"cost": "", "name": "カソナード", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "アーモンドパウダー", "unit": "g", "groupId": "default", "quantity": 83, "purchaseCost": ""}, {"cost": "", "name": "バニラビーンズ", "unit": "/3本", "groupId": "default", "quantity": 1, "purchaseCost": ""}, {"cost": "", "name": "全卵", "unit": "g", "groupId": "default", "quantity": 66, "purchaseCost": ""}, {"cost": "", "name": "強力粉", "unit": "g", "groupId": "default", "quantity": 208, "purchaseCost": 280}, {"cost": "", "name": "ベーキングパウダー", "unit": "g", "groupId": "default", "quantity": 2, "purchaseCost": ""}, {"cost": "", "name": "アーモンドクリーム", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "バター", "unit": "g", "groupId": "default", "quantity": 42, "purchaseCost": ""}, {"cost": "", "name": "粉糖", "unit": "g", "groupId": "default", "quantity": 42, "purchaseCost": 280}, {"cost": "", "name": "全卵", "unit": "g", "groupId": "default", "quantity": 20, "purchaseCost": ""}, {"cost": "", "name": "アーモンドパウダー + くるみパウダー", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "薄力粉", "unit": "g", "groupId": "default", "quantity": 8, "purchaseCost": 280}, {"cost": "", "name": "塩極少量", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "ラム酒", "unit": "ml", "groupId": "default", "quantity": 6, "purchaseCost": ""}, {"cost": "", "name": "カスタードクリーム", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "牛乳", "unit": "g", "groupId": "default", "quantity": 330, "purchaseCost": 245}, {"cost": "", "name": "バニラビーンズ 2/3 本", "unit": "", "groupId": "default", "quantity": "", "purchaseCost": ""}, {"cost": "", "name": "グラニュー糖", "unit": "g", "groupId": "default", "quantity": 130, "purchaseCost": 290}, {"cost": "", "name": "コーンスターチ", "unit": "g", "groupId": "default", "quantity": 30, "purchaseCost": ""}, {"cost": "", "name": "卵黄", "unit": "g", "groupId": "default", "quantity": 80, "purchaseCost": ""}, {"cost": "", "name": "バター", "unit": "g", "groupId": "default", "quantity": 30, "purchaseCost": ""}]', '{タルト生地をつくる,強力粉とベーキングパウダーを合わせておく。,ボウルでバターを攪拌し、材料の上から順番に加えて混ぜていく。,冷蔵庫で一晩寝かせる。,アーモンドクリームをつくる,バターを攪拌し、他の材料を上から順番に加えて混ぜていく。,カスタードクリームをつくる,牛乳にバニラビーンズを入れ、温める。,グラニュー糖、コーンスターチ、卵黄をボウルで混ぜておき、温めた牛乳と合わせて火にかけていく。,火を止めてバターを加え混ぜ、バットにあけて表面にラップをぴったりと密着させて氷水にあてて冷ます。,"タルト生地を5mm にのばして型に敷き、アーモンドクリームを厚さが5mmになるように入れ、その上にカスタードクリームを厚さ1cm 分入れる。",上にタルト生地をのせて、ふたをするような感じで閉じる。,表面にフォークや串などで格子状に模様をつけ、空気穴をあけて、卵黄を刷毛で塗り、180℃のオーブンで35~45分焼く。}', '2026-01-03 18:51:05.973502+00', 'プティフール', 'URL取り込み,お菓子,デザート', 'BISTRO CAVACAVA', 201),
