	(114, 'アンチョビバター', '', 'https://hocbnifuactbvmyjraxy.supabase.co/storage/v1/object/public/recipe-images/263aad99-a38d-4cec-9ed7-624320cd2136_0.jpg', '', '', '', '{owner:admin,"バター,ソース"}', '[{"type": "normal", "_meta": true, "groups": [{"id": "default", "name": "材料"}], "stepGroups": [{"id": "default", "name": "作り方", "count": 5}]}, {"cost": "", "name": "バター", "unit": "ポンド", "groupId": "default", "quantity": 1, "purchaseCost": ""}, {"cost": "", "name": "アンチョビフィレ.", "unit": "g", "groupId": "default", "quantity": 110, "purchaseCost": ""}, {"cost": "", "name": "レモン汁", "unit": "ml", "groupId": "default", "quantity": 15, "purchaseCost": ""}, {"cost": "", "name": "胡椒", "unit": "", "groupId": "default", "quantity": "適量", "purchaseCost": ""}]', '{バターを室温に戻してポマード状にする,アンチョビの油を切り、分量の1/6程度のバターと共にフードプロセッサーにかける,2をボウルに移し、残りのバターを加えて泡立て器で混ぜ合わせる。タミで濾す,レモン汁と胡椒を加え、味を整える。,硫酸紙を敷いたバットに広げ、トントンと空気を除いてから表面を平らにならす。表面にも硫酸紙をかぶせる}', '2026-01-03 18:51:40.609319+00', '', 'バター,ソース', 'BISTRO CAVACAVA', 163),
